          trigger: 'blur'
        }]
      }
    };
  },
  methods: {
    show() {
      this.visible = true;
      this.resetForm('ruleForm');
    },
    hide() {
      this.visible = false;
    },
    goToLoginDialog() {
      this.hide();
      EventBus.$emit('show-login-dialog');
    },
    async submitForm(formName) {
      this.form = this.ruleForm, this.$refs[formName].validate(valid => {
        if (valid) {
          request.post('/log/register', this.form).then(response => {
            const {
              code,
              msg,
              data
            } = response.data;
            if (code === 1) {
              // 请求成功  
              console.log('请求成功:', msg);
              console.log('数据:', data);
              alert(msg);
              this.goToLoginDialog();
            } else if (code === 0) {
              // 请求失败  
              console.error('请求失败:', msg);
              alert(msg);
            } else {
              // 未知的响应码  
              console.error('未知的响应码:', code, msg);
            }
          }).catch(error => {
            // 处理网络错误或请求未发送成功的情况  
            if (error.response) {
              // 请求已发出，但服务器响应的状态码不在 2xx 范围内  
              console.error('服务器响应错误:', error.response.data);
              console.error('状态码:', error.response.status);
            } else if (error.request) {
              // 请求已发出，但没有收到响应  
              console.error('请求错误:', error.request);
            } else {
              // 发生了其他错误（例如，在设置请求时出错）  
              console.error('发生错误:', error.msg);
            }
          });
        } else {
          console.log('不合法的表单');
          return false;
        }
      });
    },
    resetForm(formName) {
      this.$refs[formName].resetFields();
    }
  }
};
